var SchemaObject = require('schema-object');


/**
 Smaato: Text Ad
 clickText, clickUrl, toolTip, additionalText, beacons
 */

var definition = {
  clickText:         { type: String, required: true },
  clickUrl:         { type: String, required: true },
  toolTip:          { type: String, required: true },
  additionalText:   { type: String, required: true },
  beacons:          [ { type : require('./beacon').definition } ],
};

module.exports.definition = definition;
module.exports.schema = new SchemaObject(definition);;