var SchemaObject = require('schema-object');
var mongoose = require('mongoose');

/**
Ad
*/
var models = require('../rtb')

var Banner = models.Banner;
var Video = models.Video;
var Native = models.Native;
var Audio = models.Audio;

var definition = {
  ad_id:String,
  ad_type:String,
  creative_id:String,
  title:String,
  description:String,
  banner: Banner.definition,
  video: Video.definition,
  native: Native.definition,
  audio: Audio.definition
};

var db_schema = new mongoose.Schema(definition);

module.exports.definition = definition;
module.exports.schema = new SchemaObject(definition);
module.exports.db_model = mongoose.model("Ad",db_schema);