var SchemaObject = require('schema-object');
var mongoose = require('mongoose');

/**
Reciept
*/


var definition = {
  bid_id:String,
  bidrequest_id:String,
  auction_id:String,
  campaign_id:String,
  displaymanager:String,
  imp_id:String,
  seat_id:String,
  ad_id:String,
  bid_amount:Number,
  win_amount:Number,
  won:Boolean
};

var db_schema = new mongoose.Schema(definition);

module.exports.definition = definition;
module.exports.schema = new SchemaObject(definition);
module.exports.db_model = mongoose.model("Reciept",db_schema)