
var Enum = require('enum');

/**
 No-Bid Reason Codes
 The following table lists the options for a bidder to signal the exchange as to why it did not offer a bid for the impression.
 */

module.exports.NoBidReasonCode = new Enum({
  UnknownError:               0,
  TechnicalError:             1,
  InvalidRequest:             2,
  KnownWebSpider:             3,
  SuspectedNonHumanTraffic:   4,
  CloudDataCenterOrProxyIP:   5,
  UnsupportedDevice:          6,
  BlockedPublisherOrSite:     7,
  UnmatchedUser:              8
});
