/**
Account
*/

module.exports = function  (sequelize, DataTypes) {
  var Account = sequelize.define('Account',
  {
    accountId:   { type: DataTypes.UUID, primaryKey: true, defaultValue: DataTypes.UUIDV4 },
    name:         { type:DataTypes.STRING, allowNull:false }
  },
  {
    classMethods:{
      associate: function (models) {
        Account.hasMany(models.Campaign, {as:"campaigns"});
      }
    }
  });
  return Account;
}