/**
Ad
*/

module.exports = function  (sequelize, DataTypes) {
  var Ad = sequelize.define('Ad',
  {
    adId:        { type: DataTypes.UUID,primaryKey: true, defaultValue: DataTypes.UUIDV4 },
    adType:      { type: DataTypes.INTEGER, allowNull:false },
    creativeId:  { type: DataTypes.STRING, allowNull:false },
    title:        { type:DataTypes.STRING },
    description:  { type:DataTypes.STRING }
  },
  {
    classMethods:{
      associate: function (models) {
        Ad.belongsTo( models.Campaign, { allowNull:false });
        Ad.hasOne( models.Audio, {allowNull:true});
        Ad.hasOne( models.Banner, {allowNull:true});
        Ad.hasOne( models.Native, {allowNull:true});
        Ad.hasOne( models.Video, {allowNull:true});
      }
    }
  });
  return Ad;
}