/**
Native
*/

module.exports = function  (sequelize, DataTypes) {
  var Native = sequelize.define('Native',
  {
    id:           { type: DataTypes.UUID,primaryKey: true, defaultValue: DataTypes.UUIDV4 },
    title:        { type: DataTypes.STRING },
    description:  { type: DataTypes.STRING },
    request:      { type: DataTypes.STRING, required: true },
    ver:          { type: DataTypes.STRING },
    api:          { type: DataTypes.ARRAY( DataTypes.INTEGER )},
    battr:        { type: DataTypes.ARRAY( DataTypes.INTEGER )},
  },
  {
    classMethods:{
      associate: function (models) {
        Native.belongsToMany(models.Ad, { through:"native_ads", allowNull:false });
      }
    }
  });
  return Native;
}