/**
Reciept
*/

module.exports = function  (sequelize, DataTypes) {
  var Reciept = sequelize.define('Reciept',
  {
    id:             { type: DataTypes.UUID,primaryKey: true, defaultValue: DataTypes.UUIDV4 },
    bidId: { type:DataTypes.STRING },
    bidRequestId: { type:DataTypes.STRING },
    auctionId: { type:DataTypes.STRING },
    campaignId: { type:DataTypes.STRING },
    impId: { type:DataTypes.STRING },
    seatId: { type:DataTypes.STRING },
    adId: { type:DataTypes.STRING },
    bidAmount: { type:DataTypes.DOUBLE },
    winAmount: { type:DataTypes.DOUBLE },
    won: { type:DataTypes.BOOLEAN, default: false },
    displayManager: { type:DataTypes.STRING },
  },
  {
    classMethods:{
      associate: function (models) {
        Reciept.belongsTo(models.Campaign, { allowNull:false });
      }
    }
  });
  return Reciept;
}