/**
Target
*/

module.exports = function  (sequelize, DataTypes) {
  var Target = sequelize.define('Target',
  {
    geographic:       { type: DataTypes.STRING },
  //  geopoint:         { type: DataTypes.GEOMETRY('Point') }, 
    deviceType:       { type: DataTypes.INTEGER },
    startYOB:         { type: DataTypes.STRING, length:4 },
    endYOB:           { type: DataTypes.STRING, length:4 },
    gender:           { type: DataTypes.ENUM('m','f', 'other') },
    timezone:         { type: DataTypes.STRING },
    time:             { type: DataTypes.INTEGER },
    publisher:        { type: DataTypes.ARRAY( DataTypes.STRING ) },
    contentCategory:  { type: DataTypes.ARRAY( DataTypes.STRING ) }, 
  }, 
  {
    classMethods:{
      associate: function (models) {
        Target.belongsTo(models.Campaign, { allowNull:false });
      }
    }
  });
  return Target;
}